    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Hello AccountPasswordController!{% endblock %}

{% block body %}
<h1>Modifier mon mot de passe</h1>
<a href=\"{{path('account')}}\">- Retourner au compte</a><br>
    
<hr>
    {% for label, messages in app.flashes %}
    {% for message in messages %}
    <div class=\"alert alert-{{ label }} alert-dismissible fade show\" role=\"alert\">
        {% if label == 'success' %}
        <span class=\"alert-inner--icon\"><i class=\"fe fe-thumbs-up\"></i></span>
        {% else %}
        <span class=\"alert-inner--icon\"><i class=\"fe fe-thumbs-down\"></i></span>
        {% endif %}
        <span class=\"alert-inner--text\"><strong>{{ label | title }} : </strong> {{ message | raw }}</span>
        <button type=\"button\" class=\"btn btn-primary close\" data-dismiss=\"alert\" aria-label=\"Close\">
            <span aria-hidden=\"true\">×</span>
        </button>
    </div>
    {% endfor %}
    {% endfor %}
{{form(form)}}
<hr>
{% endblock %}
", "account/account_password.html.twig", "C:\\wamp64\\www\\my_fisrt_project\\templates\\account\\account_password.html.twig");
